		VALUES(SEQ_PLACE_NO.NEXTVAL
				, #{placeName}
				, #{placeAddress}
    			, #{placeLat}
    			, #{placeLon}
    			, #{placeDes}
    			, #{placeTags}
    			, #{area}
    			, #{typeCode}
    			, #{picOrigin}
    			, #{picChange})

mapper.update:
UPDATE PLACE 
SET 
	PLACE_NAME = #{placeName}
	,PLACE_ADDRESS = #{placeAddress}
	,PLACE_DES = #{placeDes}
	,PLACE_TAGS = #{placeTags}
	,PLACE_LAT = #{placeLat}
	,PLACE_LON = #{placeLon}
	,AREA_NO = #{area}
	,TYPE_CODE = #{typeCode}
	,PIC_ORIGIN = #{picOrigin}
	,PIC_CHANGE = #{picChange}
	,MODIFY_DATE = SYSDATE
WHERE PLACE_NO = #{placeNo}

mapper.select:
SELECT PLACE_NO, PLACE_NAME, PLACE_AREA AS AREA , PLACE_TYPE AS TYPE, PLACE_TAGS, PLACE_LON, PLACE_LAT, FILE_PATH, PIC_CHANGE FROM VW_PLACE WHERE ROWNUM < 5 AND (PLACE_TAGS LIKE '%'||#{tag}||'%'
AND PLACE_AREA = #{area}
)
